import React, { useState } from "react";
import { Container, Row, Button } from "reactstrap";
import { NavLink, Link } from "react-router-dom";
import car2 from './images/car3.jpg';
import { RiMenuLine } from 'react-icons/ri'; // Import hamburger icon
import "./cute.css";

const Header = () => {
  const [isOpen, setIsOpen] = useState(false); // State to track mobile menu

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const Nav_links = [
    {
      path: "/home",
      display: "Home",
    },
    {
      path: "/about",
      display: "About",
    },
    {
      path: "/tour",
      display: "Tour",
    },
  ];

  return (
    <div className="container-fluid box">
      <header className="header container-fluid">
        <Container>
          <Row>
            <div className="nav_wrapper d-flex align-items-center justify-content-between">
              {/* Logo */}
              <div className="logo">
                <img src={car2} alt="" />
              </div>

              {/* Hamburger Menu Icon */}
              <div className="mobile_menu" onClick={toggleMenu}>
                <RiMenuLine />
              </div>

              {/* Menu */}
              <div className={`navigation ${isOpen ? 'open' : ''}`}>
                <ul className="menu d-flex gap-4 align-items-center">
                  {Nav_links.map((item, index) => (
                    <li className="nav_item" key={index}>
                      <NavLink
                        to={item.path}
                        className={(navClass) =>
                          navClass.isActive ? "active__link" : ""
                        }
                        onClick={toggleMenu} // Close menu on link click
                      >
                        {item.display}
                      </NavLink>
                    </li>
                  ))}
                </ul>
              </div>

              {/* Buttons */}
              <div className="nav_right d-flex align-items-center gap-4">
                <div className="nav_btns d-flex align-items-center gap-4">
                  <Button className="btn secondary__btn">
                    <Link to="/login">Login</Link>
                  </Button>
                  <Button className="btn primary__btn">
                    <Link to="/register">Register</Link>
                  </Button>
                </div>
              </div>
            </div>
          </Row>
        </Container>
      </header>
    </div>
  );
};

export default Header;
