import React, { useState, useEffect } from "react";
import { Container, Row, Button } from "reactstrap";
import { NavLink, Link } from "react-router-dom";
import car2 from './images/car3.jpg'

import "./cute.css";

const Header = () => {
  const [showNavbar, setShowNavbar] = useState(true); // State to manage navbar visibility

  useEffect(() => {
    // Function to handle scroll event
    const handleScroll = () => {
      const currentScrollPos = window.pageYOffset;
      const visible = currentScrollPos < 100; // Change '100' to the scroll position where you want to hide the navbar
      setShowNavbar(visible);
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []); // Empty dependency array to run this effect only once on component mount

  const Nav_links = [
    {
      path: "/home",
      display: "Home",
    },
    {
      path: "/about",
      display: "About",
    },
    {
      path: "/tour",
      display: "Tour",
    },
  ];

  return (
    <div className={`container-fluid box ${showNavbar ? '' : 'hide-navbar'}`}>
      <header className="header container-fluid">
        <Container>
          <Row>
            <div className="nav_wrapper d-flex align-items-center justify-content-between">
              {/* ===========Logo============== */}
              <div className="logo">
                <img src={car2} alt="" />
              </div>

              {/* =============Logo End============ */}
              {/* =============Menu Start============ */}

              {/* =============Menu End============ */}

              <div className="nav_right d-flex align-items-center gap-4">
                <div className="nav_btns d-flex align-items-center gap-4">
                  <Button className="btn secondary__btn">
                    <Link to="/login">Login</Link>
                  </Button>
                  <Button className="btn primary__btn">
                    <Link to="/register">Register</Link>
                  </Button>
                </div>
                <span className="mobile_menu">
                  <i className="ri-menu-line"></i>
                </span>
              </div>
            </div>
          </Row>
        </Container>
      </header>
    </div>
  );
};

export default Header;
